package operators;

public class Decrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=11;
		int y=22;
		
		System.out.println("Value of x :"+x);
		System.out.println("Value of y :"+y);
		
		System.out.println("--x :"+(--x));
		System.out.println("y-- :"+y--);
		System.out.println("Value of y :"+y);

	}

}
