package operators;

public class Increment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=11;
		int b=22;
		
		System.out.println("Value of a :"+a);
		System.out.println("Value of b :"+b);
		
		System.out.println("++a :"+(++a));
		System.out.println("b++ :"+b++);
		System.out.println("++b :"+b);
	}

}
